import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {

  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editingText, setEditingText] = useState("");

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [isRegistering, setIsRegistering] = useState(false);


  const getApi = () => {
    return axios.create({
      baseURL: "http://localhost:5000",
      headers: token ? { Authorization: `Bearer ${token}` } : {},
    });
  };


  const handleRegister = () => {
  if (!username || !password) return;

  axios
    .post("http://localhost:5000/register", { username, password })
    .then(res => {
      alert("Usuário criado com sucesso! Faça login agora.");
      setIsRegistering(false); // volta para a tela de login
      setUsername("");
      setPassword("");
    })
    .catch(err => console.error("Erro ao registrar usuário:", err));
};

  const handleLogin = () => {
    if (!username || !password) return;

    axios
      .post("http://localhost:5000/login", { username, password })
      .then((res) => {
        const token = res.data.token;
        setToken(token);
        localStorage.setItem("token", token);
        setUsername("");
        setPassword("");
      })
      .catch((err) => console.error("Erro ao fazer login:", err));
  };

  const handleLogout = () => {
    setToken("");
    localStorage.removeItem("token");
    setTasks([]);
  };


  useEffect(() => {
    if (!token) return;

    getApi()
      .get("/tasks")
      .then((res) => setTasks(res.data))
      .catch((err) => console.error("Erro ao buscar tarefas:", err));
  }, [token]);


  const addTask = () => {
    if (!newTask) return;

    getApi()
      .post("/tasks", { title: newTask })
      .then((res) => {
        setTasks([...tasks, res.data]);
        setNewTask("");
      })
      .catch((err) => console.error("Erro ao adicionar tarefa:", err));
  };


  const toggleDone = (taskId, done) => {
    getApi()
      .put(`/tasks/${taskId}`, { done: !done })
      .then((res) => {
        setTasks(tasks.map((task) => (task._id === taskId ? res.data : task)));
      })
      .catch((err) => console.error("Erro ao atualizar tarefa:", err));
  };


  const deleteTask = (taskId) => {
    getApi()
      .delete(`/tasks/${taskId}`)
      .then(() => setTasks(tasks.filter((task) => task._id !== taskId)))
      .catch((err) => console.error("Erro ao deletar tarefa:", err));
  };


  const startEdit = (task) => {
    setEditingTaskId(task._id);
    setEditingText(task.title);
  };

  const saveEdit = (taskId) => {
    getApi()
      .put(`/tasks/${taskId}`, { title: editingText })
      .then((res) => {
        setTasks(tasks.map((task) => (task._id === taskId ? res.data : task)));
        setEditingTaskId(null);
        setEditingText("");
      })
      .catch((err) => console.error("Erro ao editar tarefa:", err));
  };


  return (
    <div>
      <h1>Minha ToDo List</h1>

      {!token ? (
  <div>
    {isRegistering ? (
      <>
        <h2>Registrar novo usuário</h2>
        <input
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          placeholder="Senha"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleRegister}>Registrar</button>
        <p>
          Já tem conta?{" "}
          <span
            style={{ color: "blue", cursor: "pointer" }}
            onClick={() => setIsRegistering(false)}
          >
            Faça login
          </span>
        </p>
      </>
    ) : (
      <>
        <h2>Login</h2>
        <input
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          placeholder="Senha"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
        <p>
          Não tem conta?{" "}
          <span
            style={{ color: "blue", cursor: "pointer" }}
            onClick={() => setIsRegistering(true)}
          >
            Registre-se
          </span>
        </p>
      </>
    )}
  </div>
) : (
  <div>
    <button onClick={handleLogout}>Logout</button>

          <div style={{ marginTop: "20px" }}>
            <input
              type="text"
              placeholder="Digite uma tarefa..."
              value={newTask}
              onChange={(e) => setNewTask(e.target.value)}
            />
            <button onClick={addTask}>Adicionar</button>
          </div>

          <ul style={{ marginTop: "20px" }}>
            {tasks.map((task) => (
              <li
                key={task._id}
                style={{
                  textDecoration: task.done ? "line-through" : "none",
                  cursor: "pointer",
                  marginBottom: "10px",
                }}
              >
                {editingTaskId === task._id ? (
                  <>
                    <input
                      value={editingText}
                      onChange={(e) => setEditingText(e.target.value)}
                    />
                    <button onClick={() => saveEdit(task._id)}>Salvar</button>
                    <button onClick={() => setEditingTaskId(null)}>Cancelar</button>
                  </>
                ) : (
                  <>
                    <span onClick={() => toggleDone(task._id, task.done)}>
                      {task.title} - {task.done ? "✅" : "❌"}
                    </span>
                    <button
                      onClick={() => startEdit(task)}
                      style={{ marginLeft: "10px" }}
                    >
                      Editar
                    </button>
                  </>
                )}
                <button
                  onClick={() => deleteTask(task._id)}
                  style={{ marginLeft: "10px" }}
                >
                  Deletar
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
