import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editingText, setEditingText] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/tasks")
      .then(response => {
        setTasks(response.data);
      })
      .catch(err => {
        console.error("Erro ao buscar tarefas:", err);
      });
  }, []);
  const addTask = () => {
    if(!newTask) return;

    axios.post("http://localhost:5000/tasks", {title: newTask})
    .then(response =>{
      setTasks([...tasks, response.data]);
      setNewTask("");
    }).catch(err => {
      console.error("Erro ao adicionar tarefa:", err);
    });
  }

  const toggleDone = (taskId, done) => {
    axios.put(`http://localhost:5000/tasks/${taskId}`, { done: !done })
      .then(response => {
        setTasks(tasks.map(task => 
          task._id === taskId ? response.data : task
        ));
      })
      .catch(err => {
        console.error("Erro ao atualizar tarefa:", err);
      });
  }

  const deleteTask = (taskId) => {
    axios.delete(`http://localhost:5000/tasks/${taskId}`)
    .then(()=> {
      setTasks(tasks.filter(task => task._id !== taskId));
    })
    .catch(err => {
      console.error("Erro ao deletar tarefa:", err);
    });
  }

  const startEdit = (task) =>{
    setEditingTaskId(task._id);
    setEditingText(task.title);
  }

  const saveEdit = (taskId) => {
    axios.put(`http://localhost:5000/tasks/${taskId}`, { title: editingText })
      .then(response => {
        setTasks(tasks.map(task =>
          task._id === taskId ? response.data : task
        ));
        setEditingTaskId(null);
        setEditingText("");
      })
      .cath(err => {
        console.error("Erro ao editar tarefa:", err);
      });

  };

  return (
    <div>
      <h1>Minha ToDo List</h1>
      <input type="text" placeholder="Digite uma tarefa..." 
        value = {newTask} 
        onChange ={e => setNewTask(e.target.value)}/>
      <button onClick={addTask}>Adicionar</button>
      <ul>
        {tasks.map(task => (
          <li key={task._id}
              style={{ textDecoration: task.done ? "line-through" : "none", cursor: "pointer" }}>
              {editingTaskId === task._id ? (
                <>
                  <input
                    type="text"
                    value={editingText}
                    onChange={(e) => setEditingText(e.target.value)}
                  />
                  <button onClick = {()=> saveEdit(task._id)}>Salvar</button>
                  <button onClick = {()=> setEditingTaskId(null)}>Cancelar</button>
                </>
               ): (
                <>
                 <span onClick={() => toggleDone(task._id, task.done)}>
                   {task.title} - {task.done ? "✅" : "❌"}
                 </span>
                 <button onClick={()=> startEdit(task)} style = {{marginLeft: "10px"}}>Editar</button>
                </>
               )}  

              <button onClick={(e) => {e.stopPropagation(); deleteTask(task._id);}} style={{ marginLeft : "10px"}}>Deletar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
